// src/pages/Collections.jsx
import React, { useState, useEffect } from 'react';
import SofaLoader from '../components/SofaLoader';
import SawdustParticles from '../components/SawdustParticles';

const categories = ['All', 'Modern', 'Vintage', 'Minimalist', 'Luxury', 'Compact'];

const allCollections = [
  { title: 'Modern 3-Seater Sofa', category: 'Modern', description: 'Simple. Elegant. Spacious.', image: 'https://via.placeholder.com/400x300?text=Modern+Sofa' },
  { title: 'Vintage Armchair', category: 'Vintage', description: 'Classic curves and cozy feel.', image: 'https://via.placeholder.com/400x300?text=Vintage+Armchair' },
  { title: 'Minimalist Coffee Table', category: 'Minimalist', description: 'Function meets design.', image: 'https://via.placeholder.com/400x300?text=Minimalist+Table' },
  { title: 'Luxe Sectional', category: 'Luxury', description: 'Luxury seating for large spaces.', image: 'https://via.placeholder.com/400x300?text=Luxe+Sectional' },
  { title: 'Compact Loveseat', category: 'Compact', description: 'Perfect for small apartments.', image: 'https://via.placeholder.com/400x300?text=Compact+Loveseat' },
  { title: 'Statement Lounge', category: 'Luxury', description: 'Standout design piece.', image: 'https://via.placeholder.com/400x300?text=Statement+Lounge' },
];

const Collections = () => {
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [hoverIndex, setHoverIndex] = useState(null);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 3000);
    return () => clearTimeout(timer);
  }, []);

  const filteredCollections =
    selectedCategory === 'All'
      ? allCollections
      : allCollections.filter(item => item.category === selectedCategory);

  if (loading) {
    return <SofaLoader />;
  }

  return (
    <section className="relative max-w-7xl mx-auto px-6 py-10 font-sans bg-[#fefaf3] overflow-hidden">
      {/* Sawdust particles overlay */}
      <SawdustParticles />

      <h2 className="text-4xl font-extrabold text-gray-900 mb-8 text-center tracking-tight z-10 relative">
        Discover Our Collections
      </h2>

      {/* Filter Bar */}
      <div className="flex justify-center space-x-4 mb-12 relative z-10">
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            className={`px-5 py-2 rounded-full text-sm font-semibold transition-shadow
              ${
                selectedCategory === cat
                  ? 'bg-amber-600 text-white shadow-lg'
                  : 'bg-gray-200 text-gray-700 hover:bg-amber-300 hover:text-white'
              }
            `}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Grid Cards */}
      <div className="grid gap-12 sm:grid-cols-2 md:grid-cols-3 relative z-10">
        {filteredCollections.map(({ title, description, image }, i) => (
          <div
            key={i}
            onMouseEnter={() => setHoverIndex(i)}
            onMouseLeave={() => setHoverIndex(null)}
            className="relative rounded-2xl shadow-2xl bg-white overflow-hidden transform transition-transform duration-300 hover:-translate-y-2 hover:shadow-amber-400 hover:shadow-lg hover:scale-[1.03] cursor-pointer"
          >
            <div className="relative h-64 overflow-hidden">
              {/* Wood Texture Overlay */}
              <img
                src="https://www.transparenttextures.com/patterns/wood-pattern.png"
                alt=""
                className="absolute top-0 left-0 w-full h-full object-cover opacity-30 pointer-events-none"
                style={{ mixBlendMode: 'multiply' }}
              />
              <img
                src={image}
                alt={title}
                className="w-full h-full object-cover"
              />
              {/* Nail Sparks */}
              {hoverIndex === i && (
                <div className="absolute top-3 right-3 w-6 h-6 pointer-events-none">
                  <svg
                    className="animate-sparkle"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <circle cx="12" cy="12" r="5" stroke="#FFC107" strokeWidth="2" />
                    <line
                      x1="12"
                      y1="1"
                      x2="12"
                      y2="6"
                      stroke="#FFC107"
                      strokeWidth="2"
                      strokeLinecap="round"
                    />
                    <line
                      x1="12"
                      y1="18"
                      x2="12"
                      y2="23"
                      stroke="#FFC107"
                      strokeWidth="2"
                      strokeLinecap="round"
                    />
                    <line
                      x1="1"
                      y1="12"
                      x2="6"
                      y2="12"
                      stroke="#FFC107"
                      strokeWidth="2"
                      strokeLinecap="round"
                    />
                    <line
                      x1="18"
                      y1="12"
                      x2="23"
                      y2="12"
                      stroke="#FFC107"
                      strokeWidth="2"
                      strokeLinecap="round"
                    />
                  </svg>
                </div>
              )}
            </div>
            <div className="p-6">
              <h3 className="text-2xl font-bold text-gray-900 mb-2">{title}</h3>
              <p className="text-gray-600">{description}</p>
            </div>
          </div>
        ))}
      </div>

      <style jsx>{`
        .animate-sparkle {
          animation: sparkle 1s ease-in-out infinite;
        }
        @keyframes sparkle {
          0%, 100% { opacity: 1; transform: scale(1); }
          50% { opacity: 0.3; transform: scale(1.2); }
        }
      `}</style>
    </section>
  );
};

export default Collections;
