// src/pages/Collections.jsx
import React, { useState, useEffect } from 'react';
import SofaLoader from '../components/SofaLoader';

const categories = ['All', 'Modern', 'Vintage', 'Minimalist', 'Luxury', 'Compact'];

const allCollections = [
  { title: 'Modern 3-Seater Sofa', category: 'Modern', description: 'Simple. Elegant. Spacious.', image: 'https://via.placeholder.com/400x300?text=Modern+Sofa' },
  { title: 'Vintage Armchair', category: 'Vintage', description: 'Classic curves and cozy feel.', image: 'https://via.placeholder.com/400x300?text=Vintage+Armchair' },
  { title: 'Minimalist Coffee Table', category: 'Minimalist', description: 'Function meets design.', image: 'https://via.placeholder.com/400x300?text=Minimalist+Table' },
  { title: 'Luxe Sectional', category: 'Luxury', description: 'Luxury seating for large spaces.', image: 'https://via.placeholder.com/400x300?text=Luxe+Sectional' },
  { title: 'Compact Loveseat', category: 'Compact', description: 'Perfect for small apartments.', image: 'https://via.placeholder.com/400x300?text=Compact+Loveseat' },
  { title: 'Statement Lounge', category: 'Luxury', description: 'Standout design piece.', image: 'https://via.placeholder.com/400x300?text=Statement+Lounge' },
];

const Collections = () => {
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 3000);
    return () => clearTimeout(timer);
  }, []);

  const filteredCollections =
    selectedCategory === 'All'
      ? allCollections
      : allCollections.filter(item => item.category === selectedCategory);

  if (loading) {
    return <SofaLoader />;
  }

  return (
    <section className="relative bg-[#fffaf3] min-h-screen py-16 px-6 max-w-7xl mx-auto font-sans">
      {/* Background subtle geometric shapes */}
      <svg
        className="pointer-events-none absolute top-0 left-0 w-full h-full -z-10"
        aria-hidden="true"
      >
        <circle cx="20%" cy="15%" r="120" fill="#f9e8d6" />
        <circle cx="80%" cy="85%" r="180" fill="#f4d8b3" />
        <rect x="50%" y="40%" width="250" height="250" fill="#f3c77a" rx="40" opacity="0.3" />
      </svg>

      <h1 className="text-5xl font-extrabold text-amber-900 mb-12 text-center tracking-wide drop-shadow-md">
        Our Collections
      </h1>

      {/* Filter Bar */}
      <div className="flex justify-center gap-4 flex-wrap mb-16">
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            className={`px-6 py-3 rounded-full font-semibold transition-colors duration-300
              ${
                selectedCategory === cat
                  ? 'bg-amber-700 text-white shadow-lg scale-105'
                  : 'bg-white text-amber-700 border border-amber-700 hover:bg-amber-700 hover:text-white'
              }
            `}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Product Cards */}
      <div className="grid gap-12 sm:grid-cols-2 md:grid-cols-3">
        {filteredCollections.map(({ title, description, image }, i) => (
          <div
            key={i}
            className="bg-white rounded-3xl shadow-lg overflow-hidden cursor-pointer hover:scale-[1.04] hover:shadow-2xl transition-transform duration-300"
            tabIndex={0}
            aria-label={`View details of ${title}`}
          >
            <img
              src={image}
              alt={title}
              className="w-full h-64 object-cover"
              loading="lazy"
            />
            <div className="p-6">
              <h3 className="text-2xl font-bold text-amber-900 mb-2">{title}</h3>
              <p className="text-gray-600">{description}</p>
              <button
                type="button"
                className="mt-4 inline-block bg-amber-700 text-white px-6 py-3 rounded-xl font-semibold hover:bg-amber-800 transition"
              >
                Shop Now
              </button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Collections;
