// src/pages/Homepage.jsx
import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const Homepage = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timeout = setTimeout(() => setLoading(false), 3000);
    return () => clearTimeout(timeout);
  }, []);

  if (loading) {
    return (
      <div className="h-screen w-screen flex flex-col items-center justify-center bg-[#f8f1e4]">
        <div className="w-24 h-24 border-4 border-dashed border-amber-800 rounded-full animate-spin mb-4" />
        <p className="text-xl font-medium text-amber-900">Building your sofa...</p>
      </div>
    );
  }

  return (
    <>
      <Navbar />
      <main className="bg-[#fffaf3]">
        {/* Hero */}
        <section className="h-screen flex items-center justify-center text-center px-6 bg-[url('/sofa-hero.jpg')] bg-cover bg-center relative">
          <div className="absolute inset-0 bg-black/40" />
          <div className="z-10 text-white">
            <h1 className="text-4xl md:text-6xl font-bold mb-4">Timeless Wooden Elegance</h1>
            <p className="text-lg md:text-xl mb-6">Handcrafted furniture built for generations.</p>
            <button className="px-6 py-3 bg-amber-700 hover:bg-amber-800 text-white rounded-lg text-lg">Explore Collection</button>
          </div>
        </section>

        {/* Featured */}
        <section className="py-16 px-6 max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-center text-gray-800 mb-12">Featured Pieces</h2>
          <div className="grid md:grid-cols-3 gap-8">
            {["Sofa", "Dining Set", "Bed Frame"].map((item, index) => (
              <div key={index} className="bg-white shadow-md rounded-xl overflow-hidden">
                <div className="h-52 bg-gray-200" />
                <div className="p-4">
                  <h3 className="text-xl font-semibold">{item}</h3>
                  <p className="text-sm text-gray-600 mt-2">Premium wood and finish. Perfect for modern homes.</p>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Craftsmanship */}
        <section className="bg-white py-20 px-6">
          <div className="max-w-6xl mx-auto grid md:grid-cols-2 items-center gap-12">
            <img src="/craftsman.jpg" alt="craftsman" className="rounded-lg shadow-lg" />
            <div>
              <h2 className="text-3xl font-bold text-gray-800 mb-4">Our Craft, Your Comfort</h2>
              <p className="text-gray-700 mb-6">We pride ourselves on using sustainably sourced hardwood and age-old joinery techniques to deliver beauty and durability.</p>
              <button className="px-6 py-3 bg-amber-700 text-white rounded-lg">Learn More</button>
            </div>
          </div>
        </section>

        {/* CTA */}
        <section className="text-center py-16 bg-amber-50 px-4">
          <h2 className="text-3xl font-bold text-gray-800 mb-4">Ready to Elevate Your Space?</h2>
          <p className="text-gray-600 mb-6">Browse our handcrafted furniture or visit our experience store today.</p>
          <button className="bg-amber-700 text-white px-6 py-3 rounded-lg hover:bg-amber-800">Get Started</button>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Homepage;
