// src/pages/Collections.jsx
import React, { useState, useEffect } from 'react';
import SofaLoader from '../components/SofaLoader';

const categories = ['All', 'Modern', 'Vintage', 'Minimalist', 'Luxury', 'Compact'];

const allCollections = [
  { title: 'Modern 3-Seater Sofa', category: 'Modern', description: 'Simple. Elegant. Spacious.', image: 'https://via.placeholder.com/400x300?text=Modern+Sofa' },
  { title: 'Vintage Armchair', category: 'Vintage', description: 'Classic curves and cozy feel.', image: 'https://via.placeholder.com/400x300?text=Vintage+Armchair' },
  { title: 'Minimalist Coffee Table', category: 'Minimalist', description: 'Function meets design.', image: 'https://via.placeholder.com/400x300?text=Minimalist+Table' },
  { title: 'Luxe Sectional', category: 'Luxury', description: 'Luxury seating for large spaces.', image: 'https://via.placeholder.com/400x300?text=Luxe+Sectional' },
  { title: 'Compact Loveseat', category: 'Compact', description: 'Perfect for small apartments.', image: 'https://via.placeholder.com/400x300?text=Compact+Loveseat' },
  { title: 'Statement Lounge', category: 'Luxury', description: 'Standout design piece.', image: 'https://via.placeholder.com/400x300?text=Statement+Lounge' },
];

const Collections = () => {
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 3000);
    return () => clearTimeout(timer);
  }, []);

  const filteredCollections =
    selectedCategory === 'All'
      ? allCollections
      : allCollections.filter(item => item.category === selectedCategory);

  if (loading) {
    return <SofaLoader />;
  }

  return (
    <section className="bg-gradient-to-b from-amber-50 to-white min-h-screen py-16 px-6 font-sans max-w-7xl mx-auto">
      <h2 className="text-5xl font-extrabold text-amber-900 mb-12 text-center tracking-wide drop-shadow-lg">
        Explore Our <span className="text-amber-700">Vibrant Collections</span>
      </h2>

      {/* Filter Bar */}
      <div className="flex justify-center flex-wrap gap-4 mb-16">
        {categories.map(cat => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            className={`px-6 py-2 rounded-full font-semibold transition-colors duration-300
              ${
                selectedCategory === cat
                  ? 'bg-amber-700 text-white shadow-lg scale-110'
                  : 'bg-white text-amber-700 border-2 border-amber-700 hover:bg-amber-700 hover:text-white'
              }
            `}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Cards with flip animation */}
      <div className="grid gap-10 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        {filteredCollections.map(({ title, description, image }, i) => (
          <div
            key={i}
            tabIndex={0}
            aria-label={`Collection: ${title}`}
            className="relative perspective cursor-pointer group"
          >
            <div className="relative w-full h-80 transition-transform duration-700 transform-style-preserve-3d group-hover:rotate-y-180 rounded-xl shadow-2xl">
              {/* Front Side */}
              <div className="absolute w-full h-full backface-hidden rounded-xl overflow-hidden shadow-lg bg-white">
                <img
                  src={image}
                  alt={title}
                  className="w-full h-full object-cover"
                  loading="lazy"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent rounded-xl" />
                <h3 className="absolute bottom-5 left-5 text-xl font-bold text-white drop-shadow-lg">{title}</h3>
              </div>
              {/* Back Side */}
              <div className="absolute w-full h-full backface-hidden rotate-y-180 bg-amber-700 rounded-xl p-6 flex flex-col justify-center text-white shadow-lg">
                <h3 className="text-2xl font-bold mb-3">{title}</h3>
                <p className="mb-6">{description}</p>
                <button className="self-start bg-white text-amber-700 font-semibold px-4 py-2 rounded-md shadow hover:bg-amber-100 transition">
                  View Details
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Collections;
