// src/components/SofaLoader.jsx
import React from "react";

const SofaLoader = () => {
  return (
    <div className="flex flex-col items-center justify-center h-screen w-screen bg-[#f8f1e4]">
      <svg
        className="w-56 h-56 mb-6"
        viewBox="0 0 200 200"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        {/* Backrest (vertical planks) */}
        <rect
          className="plank backrest1"
          x="30"
          y="40"
          width="15"
          height="80"
          fill="#A9745B"
          rx="3"
        />
        <rect
          className="plank backrest2"
          x="55"
          y="40"
          width="15"
          height="80"
          fill="#8C5E3C"
          rx="3"
        />
        <rect
          className="plank backrest3"
          x="80"
          y="40"
          width="15"
          height="80"
          fill="#B38B6D"
          rx="3"
        />

        {/* Seat (horizontal planks) */}
        <rect
          className="plank seat1"
          x="30"
          y="120"
          width="65"
          height="20"
          fill="#A9745B"
          rx="3"
        />
        <rect
          className="plank seat2"
          x="30"
          y="140"
          width="65"
          height="15"
          fill="#8C5E3C"
          rx="3"
        />

        {/* Armrests */}
        <rect
          className="plank armrest-left"
          x="15"
          y="80"
          width="15"
          height="55"
          fill="#A9745B"
          rx="3"
        />
        <rect
          className="plank armrest-right"
          x="95"
          y="80"
          width="15"
          height="55"
          fill="#8C5E3C"
          rx="3"
        />

        {/* Legs */}
        <rect
          className="plank leg-left"
          x="25"
          y="155"
          width="10"
          height="25"
          fill="#5C3A21"
          rx="2"
        />
        <rect
          className="plank leg-right"
          x="85"
          y="155"
          width="10"
          height="25"
          fill="#5C3A21"
          rx="2"
        />

        {/* Hammer */}
        <rect
          className="hammer-handle"
          x="140"
          y="50"
          width="10"
          height="50"
          fill="#6B4F4F"
          rx="2"
          transform-origin="145 100"
        />
        <rect
          className="hammer-head"
          x="130"
          y="40"
          width="30"
          height="15"
          fill="#555"
          rx="3"
          transform-origin="145 100"
        />
      </svg>

      <p className="text-xl font-medium text-amber-900">Building your sofa...</p>

      <style jsx>{`
        .plank {
          opacity: 0;
          transform: translateY(30px);
          animation-fill-mode: forwards;
          animation-timing-function: ease-out;
          transform-origin: center;
        }

        .backrest1 {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 0.2s;
        }
        .backrest2 {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 0.4s;
        }
        .backrest3 {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 0.6s;
        }
        .seat1 {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 0.8s;
        }
        .seat2 {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 1s;
        }
        .armrest-left {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 1.2s;
        }
        .armrest-right {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 1.4s;
        }
        .leg-left {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 1.6s;
        }
        .leg-right {
          animation: plankIn 0.6s forwards ease-out;
          animation-delay: 1.8s;
        }

        @keyframes plankIn {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .hammer-handle,
        .hammer-head {
          animation: hammerSwing 0.8s infinite ease-in-out;
          transform-origin: 145px 100px;
        }

        @keyframes hammerSwing {
          0% {
            transform: rotate(0deg);
          }
          25% {
            transform: rotate(-20deg);
          }
          50% {
            transform: rotate(0deg);
          }
          75% {
            transform: rotate(-10deg);
          }
          100% {
            transform: rotate(0deg);
          }
        }
      `}</style>
    </div>
  );
};

export default SofaLoader;
