import React from 'react';

const SawdustParticles = () => {
  const particles = Array.from({ length: 20 }); // fewer particles

  return (
    <svg
      className="pointer-events-none fixed inset-0 w-full h-full"
      style={{ zIndex: 9999 }}
      xmlns="http://www.w3.org/2000/svg"
    >
      {particles.map((_, i) => {
        const cx = Math.random() * 100;
        const cy = Math.random() * 100;
        const r = 1 + Math.random() * 1.5;
        const duration = 10 + Math.random() * 10;
        const delay = Math.random() * duration;

        return (
          <circle
            key={i}
            cx={`${cx}%`}
            cy={`${cy}%`}
            r={r}
            fill="rgba(160, 120, 70, 0.3)" // lighter wood tone, low opacity
            style={{
              animation: `drift ${duration}s ease-in-out infinite`,
              animationDelay: `${delay}s`,
              opacity: 0.3,
            }}
          />
        );
      })}

      <style jsx>{`
        @keyframes drift {
          0% {
            transform: translate(0, 0);
            opacity: 0.3;
          }
          25% {
            transform: translate(25px, -25px);
            opacity: 0.15;
          }
          50% {
            transform: translate(0, -50px);
            opacity: 0.1;
          }
          75% {
            transform: translate(-25px, -25px);
            opacity: 0.15;
          }
          100% {
            transform: translate(0, 0);
            opacity: 0.3;
          }
        }
      `}</style>
    </svg>
  );
};

export default SawdustParticles;
