// src/components/SofaLoader.jsx
import React from "react";

const SofaLoader = () => {
  return (
    <div className="flex flex-col items-center justify-center h-screen w-screen bg-[#f8f1e4]">
      <svg
        className="w-48 h-48 mb-6"
        viewBox="0 0 200 200"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        {/* Wooden planks */}
        <rect
          className="plank plank1"
          x="20"
          y="130"
          width="40"
          height="15"
          fill="#A9745B"
          rx="3"
        />
        <rect
          className="plank plank2"
          x="60"
          y="130"
          width="40"
          height="15"
          fill="#8C5E3C"
          rx="3"
        />
        <rect
          className="plank plank3"
          x="100"
          y="130"
          width="60"
          height="15"
          fill="#B38B6D"
          rx="3"
        />

        {/* Hammer */}
        <rect
          className="hammer-handle"
          x="130"
          y="60"
          width="10"
          height="50"
          fill="#6B4F4F"
          rx="2"
          transform-origin="135 110"
        />
        <rect
          className="hammer-head"
          x="120"
          y="50"
          width="30"
          height="15"
          fill="#555"
          rx="3"
          transform-origin="135 110"
        />
      </svg>

      <p className="text-xl font-medium text-amber-900">Building your sofa...</p>

      <style jsx>{`
        .plank {
          transform-origin: center;
          animation-fill-mode: forwards;
          opacity: 0;
          transform: translateY(30px);
          animation-timing-function: ease-out;
        }
        .plank1 {
          animation: plankIn1 1s forwards ease-out;
          animation-delay: 0.2s;
        }
        .plank2 {
          animation: plankIn2 1s forwards ease-out;
          animation-delay: 0.6s;
        }
        .plank3 {
          animation: plankIn3 1s forwards ease-out;
          animation-delay: 1s;
        }

        @keyframes plankIn1 {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes plankIn2 {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes plankIn3 {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .hammer-handle,
        .hammer-head {
          animation: hammerSwing 0.8s infinite ease-in-out;
          transform-origin: 135px 110px;
        }

        @keyframes hammerSwing {
          0% {
            transform: rotate(0deg);
          }
          25% {
            transform: rotate(-20deg);
          }
          50% {
            transform: rotate(0deg);
          }
          75% {
            transform: rotate(-10deg);
          }
          100% {
            transform: rotate(0deg);
          }
        }
      `}</style>
    </div>
  );
};

export default SofaLoader;
