// src/components/Navbar.jsx
import React, { useState } from 'react';

const Navbar = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  return (
    <header className="bg-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800">
          Furniture<span className="text-amber-700">House</span>
        </h1>

        {/* Desktop Nav */}
        <nav className="space-x-6 hidden md:flex">
          <a href="/" className="text-gray-700 hover:text-amber-700">Home</a>
          <a href="/collections" className="text-gray-700 hover:text-amber-700">Collections</a>
          <a href="/about" className="text-gray-700 hover:text-amber-700">About</a>
          <a href="/contact" className="text-gray-700 hover:text-amber-700">Contact</a>
        </nav>

        {/* Hamburger Button */}
        <button
          className="md:hidden text-gray-800 text-2xl focus:outline-none"
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          aria-label="Toggle navigation"
        >
          â˜°
        </button>
      </div>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <nav className="md:hidden px-4 pb-4 space-y-2 bg-white shadow-md">
          <a href="/" className="block text-gray-700 hover:text-amber-700">Home</a>
          <a href="/collections" className="block text-gray-700 hover:text-amber-700">Collections</a>
          <a href="/about" className="block text-gray-700 hover:text-amber-700">About</a>
          <a href="/contact" className="block text-gray-700 hover:text-amber-700">Contact</a>
        </nav>
      )}
    </header>
  );
};

export default Navbar;
